export default function Stats() {
  const stats = useStats('https://covid19.mathdro.id/api');
  console.log(stats);
  if (!stats) return <p>loading stats</p>;
  return (
    <div>
      <div className="statBlock">
        <h3>confirmed cases:</h3>
        <span>{stats[0].confirmed.value}</span>
      </div>
      <div className="statBlock">
        <h3>deaths:</h3>
        <span>{stats[0].deaths.value}</span>
      </div>
      <div className="statBlock">
        <h3>recoveries:</h3>
        <span>{stats[0].recovered.value}</span>
      </div>

    </div>
  )
}
