import useCountryStats from '../utils/useCountryStats';

export default function Stats({ url }) {
  if (url.contains('undefined')) return <p>select a country</p>;
  const stats = useCountryStats(url);

  if (!stats) return <p>loading data</p>;

  console.log('country stats', stats);

  return (
    <div>
      <div className="statBlock">
        <h3>confirmed cases:</h3>
        <span>{stats.confirmed.value}</span>
      </div>
      <div className="statBlock">
        <h3>deaths:</h3>
        <span>{stats.deaths.value}</span>
      </div>
      <div className="statBlock">
        <h3>recoveries:</h3>
        <span>{stats.recovered.value}</span>
      </div>
    </div>
  )
}
