import useCountries from '../utils/useCountries';

export default function CountrySelector() {
  const countriesData = useCountries('https://covid19.mathdro.id/api/countries');
  if (!countriesData) return <p>loading countries</p>;

  const countriesList = countriesData.countries.map((country) =>
    <li>{country}</li>
  );
  return (
    <div>
      <select>
        {Object.entries(countriesData.countries).map(([country, code]) => {
          <option value={code} key={code}>{country}</option>
        })}
      </select>
    </div>
  )
}
