import useStats from '../utils/useStats';

export default function Stats({ url }) {
  function statsConf() {
    const stats = useStats(url);
    return typeof(stats) === 'array' ? stats.confirmed.value : stats[0].confirmed.value;
  }
  function statsDeath() {
    const stats = useStats(url);
    return typeof(stats) === 'array'? stats.deaths.value : stats[0].deaths.value;
  }
  function statsRec() {
    const stats = useStats(url);
    return typeof(stats) === 'array' ? stats.recovered.value : stats[0].recovered.value;
  }

  if (!statsConf) return <p>loading stats</p>;

  // console.log(stats.confirmed);

  return (
    <div>
      <div className="statBlock">
        <h3>confirmed cases:</h3>
        <span>{statsConf()}</span>
      </div>
      <div className="statBlock">
        <h3>deaths:</h3>
        <span>{statsDeath()}</span>
      </div>
      <div className="statBlock">
        <h3>recoveries:</h3>
        <span>{statsRec()}</span>
      </div>
    </div>
  )
}
