import { useState, useEffect } from "react";

function useStats() {
  const [stats, setStats] = useState();

  useEffect(() => {
    async function fetchData() { 
      console.log('fetching data');
      const data = await fetch('https://covid19.mathdro.id/api').then(response =>
        response.json()
      );
      setStats([data]);
    };
    fetchData();
  }, []);
  return stats;
}

function Stats() {
  const stats = useStats();
  console.log(stats);
  if (!stats) return <p>loading stats</p>;
  return (
    <div>
      <div className="statBlock">
        <h3>confirmed cases:</h3>
        <span>{stats[0].confirmed.value}</span>
      </div>
      <div className="statBlock">
        <h3>deaths:</h3>
        <span>{stats[0].death.value}</span>
      </div>
      <div className="statBlock">
        <h3>recoveries:</h3>
        <span>{stats[0].recovered.value}</span>
      </div>

    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats></Stats>
    </div>
  );
}
