import { useState, useEffect } from "react";

export default function useStats(url) {
  const [stats, setStats] = useState();

  useEffect(() => {
    async function fetchData() {
      const data = await fetch(url).then(response =>
        response.json()
        );
      console.log('overall data', data);
      setStats([data]);
    };
    fetchData();
  }, []);
  return stats;
}
