import useStats from '../utils/useStats';

export default function CountrySelector() {
  const countriesData = useStats('https://covid19.mathdro.id/api/countries');
  if (!countriesData) return <p>loading countries</p>;

  const countryOptions = Object.entries(countriesData.countries).map(([country, code]) => {
    console.log(code);
    return <option value={code} key={code}>{country}</option>
  });
  return (
    <div>
      <select>
        {countryOptions}
      </select>
    </div>
  )
}
